mapper.select:
SELECT I.INGREDIENT_SEQ
  FROM TINGREDIENTHIS I
 WHERE I.INGREDIENT_NO = #{ingredientNo}
   AND I.STORE_NO = #{storeNo}
 ORDER BY I.INGREDIENT_SEQ DESC
 LIMIT 1

mapper.insert:
INSERT
  INTO TINGREDIENTHIS
(
  INGREDIENT_NO
, INGREDIENT_SEQ
, STORE_NO
, IN_DE_QUANTITY
, IN_DE_REASON_NO
, EXPIRATION_DATE
, INSERT_ID
, INSERT_DATE
, MODIFY_ID
, MODIFY_DATE
)
VALUES
(
  #{ingredientNo}
, #{ingredientSeq}
, #{storeNo}
, #{inDeQuantity}
, #{inDeReasonNo}
, #{expirationDate}
, #{insertId}
, NOW()
, #{modifyId}
, NOW()
)

mapper.update:
UPDATE TINGREDIENT I
   SET I.QUANTITY = #{quantity}
     , I.MODIFY_ID = #{modifyId}
     , I.MODIFY_DATE = NOW()
 WHERE I.STORE_NO = #{storeNo}
   AND I.INGREDIENT_NO = #{ingredientNo}
