mapper.insert:
INSERT
INTO TMENU
(
  MENU_NO
, MENU_NAME
, SALE_STATUS_NO
, CATEGORY_NO
, IMAGE_SAVE_NAME
, IMAGE_ORIGIN_NAME
, IMAGE_PATH
, IMAGE_EXT
, INSERT_ID
, INSERT_DATE
, MODIFY_ID
, MODIFY_DATE
)
VALUES
(
  #{menuNo}
, #{menuName}
, #{saleStatusNo}
, #{categoryNo}
, #{imageSaveName}
, #{imageOriginName}
, #{imagePath}
, #{imageExt}
, #{insertId}
, NOW()
, #{modifyId}
, NOW()
)

INSERT
INTO TMENUINGREDIENT
(
  MENU_NO
, INGREDIENT_NO
, STORE_NO
, QUANTITY
, INSERT_ID
, INSERT_DATE
, MODIFY_ID
, MODIFY_DATE
)
VALUES
(
  #{menuNo}
, #{ingredientNo}
, #{storeNo}
, #{quantity}
, #{insertId}
, NOW()
, #{modifyId}
, NOW()
)

INSERT
INTO TMENUPRICE
(
  MENU_NO
, MENU_SEQ
, SALE_PRICE
, INSERT_ID
, INSERT_DATE
, MODIFY_ID
, MODIFY_DATE
)
VALUES
(
  #{menuNo}
, #{menuSeq}
, #{salePrice}
, #{insertId}
, NOW()
, #{modifyId}
, NOW()
)

mapper.select:
SELECT MI.INGREDIENT_NO
     , MI.QUANTITY
FROM TMENUINGREDIENT MI
WHERE MI.MENU_NO = #{menuNo}
  AND MI.STORE_NO = #{storeNo}

mapper.update:
UPDATE TMENU M
SET M.MENU_NAME = #{menuName}
  , M.CATEGORY_NO = #{categoryNo}
  , M.SALE_STATUS_NO = #{saleStatusNo}
  , M.IMAGE_SAVE_NAME = #{imageSaveName}
  , M.IMAGE_ORIGIN_NAME = #{imageOriginName}
  , M.IMAGE_EXT = #{imageExt}
  , M.IMAGE_PATH = #{imagePath}
  , M.MODIFY_ID = #{modifyId}
  , M.MODIFY_DATE = NOW()
WHERE M.MENU_NO = #{menuNo}

UPDATE TMENU M
SET M.MENU_NAME = #{menuName}
  , M.CATEGORY_NO = #{categoryNo}
  , M.SALE_STATUS_NO = #{saleStatusNo}
  , M.MODIFY_ID = #{modifyId}
  , M.MODIFY_DATE = NOW()
WHERE M.MENU_NO = #{menuNo}

mapper.delete:
DELETE
  FROM TMENUINGREDIENT MI
 WHERE MI.STORE_NO = #{storeNo}
AND MI.MENU_NO = #{menuNo}

mapper.select:
SELECT MP.MENU_SEQ
FROM TMENUPRICE MP
WHERE MP.MENU_NO = #{menuNo}
