mapper.select:
SELECT COUNT(*)
  FROM TOPENCLOSE
 WHERE CLOSE_MONEY IS NULL;

mapper.insert:
INSERT
  INTO TOPENCLOSE
(
  OPEN_CLOSE_NO
, OPEN_CLOSE_SEQ
, OPEN_MONEY
, INSERT_ID
, INSERT_DATE
, MODIFY_ID
, MODIFY_DATE
)
VALUES
(
  #{openCloseNo}
, #{openCloseSeq}
, #{openMoney}
, #{insertId}
, NOW()
, #{modifyId}
, NOW()
)
