mapper.select:
SELECT O.QUANTITY
  FROM TORDER O
 WHERE O.STORE_NO = #{storeNo}
   AND O.TABLE_NO = #{tableNo}
   AND O.MENU_NO = #{menuNo}
   AND O.ORDER_STATUS_NO = 'OS001'

SELECT O.ORDER_NO
  FROM TORDER O
 WHERE O.STORE_NO = #{storeNo}
   AND O.TABLE_NO =  #{tableNo}
 ORDER BY O.ORDER_NO DESC
 LIMIT 1

SELECT O.ORDER_SEQ
  FROM TORDER O
 WHERE O.ORDER_NO = #{orderNo}
 ORDER BY O.ORDER_SEQ DESC
 LIMIT 1;

mapper.update:
UPDATE TORDER O
   SET O.QUANTITY = #{quantity}
     , O.TOTAL_SALE_PRICE = #{totalSalePrice}
     , O.MODIFY_ID = #{modifyId}
     , O.MODIFY_DATE = NOW()
 WHERE O.STORE_NO = #{storeNo}
   AND O.ORDER_NO = #{orderNo}
   AND O.TABLE_NO = #{tableNo}
   AND O.MENU_NO = #{menuNo}

UPDATE TORDER O
   SET O.ORDER_STATUS_NO = #{orderStatusNo}
     , O.MODIFY_ID = #{modifyId}
     , O.MODIFY_DATE = NOW()
 WHERE O.STORE_NO = #{storeNo}
   AND O.TABLE_NO = #{tableNo}
   AND O.ORDER_STATUS_NO = 'OS001'

UPDATE TORDER O
   SET O.ORDER_STATUS_NO = #{orderStatusNo}
     , O.CANCEL_DATE = NOW()
     , O.MODIFY_ID = #{modifyId}
     , O.MODIFY_DATE = NOW()
 WHERE O.STORE_NO = #{storeNo}
   AND O.ORDER_NO = #{orderNo}
   AND O.TABLE_NO = #{tableNo}
   AND O.MENU_NO = #{menuNo}
   AND O.ORDER_STATUS_NO = 'OS001'

UPDATE TORDER O
